package basic.dev;

public class MainApp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// bai tap 1: Viết chương trình tính tổng các sốtừ 1 đến 100
		int tong = 0;
		for(int i =0; i<= 100; i++)	{
			tong += i;
		}
		System.out.println("tong = "+tong );
	}
		}
